function Dalek(a){this.Speed=1;this.Look=new THREE.Vector3(0,0,this.Speed);this.RelativeCam=new THREE.Vector3(0,0,0);if(a=="red"){this.Model=model_red.clone()}else{if(a=="blue"){this.Model=model_blue.clone()}}scene.add(this.Model)}Dalek.prototype.SetPosition=function(a){this.Model.position.x=a.x;this.Model.position.z=a.y};Dalek.prototype.Destroy=function(){scene.remove(this.Model)};Dalek.prototype.GetPosition=function(){return VecSet2(this.Model.position.x,this.Model.position.z)};Dalek.prototype.SetCamera=function(){switch(CameraMode){case 0:this.RelativeCam.x=-this.Look.x;this.RelativeCam.z=-this.Look.z;this.RelativeCam.y=CamAY;this.RelativeCam.multiplyScalar(CamL);camera.position.copy(this.Model.position.clone().add(this.RelativeCam));camera.lookAt(this.Model.position.clone().add(new THREE.Vector3(0,10,0)));break;case 1:this.RelativeCam.x=this.Look.x*0.2;this.RelativeCam.z=this.Look.z*0.2;this.RelativeCam.y=18;camera.position.copy(this.Model.position.clone().add(this.RelativeCam));camera.lookAt(camera.position.clone().add(this.Look));break}};Dalek.prototype.Rotate=function(a){this.Model.rotation.y+=a;this.Look.x=this.Speed*Math.sin(this.Model.rotation.y);this.Look.z=this.Speed*Math.cos(this.Model.rotation.y)};Dalek.prototype.SetRotate=function(a){this.Model.rotation.y=a;this.Look.x=this.Speed*Math.sin(this.Model.rotation.y);this.Look.z=this.Speed*Math.cos(this.Model.rotation.y)};Dalek.prototype.Move=function(){var a=this.Model.position.clone().add(this.Look);if(DetectCollision(collision_map,5,a.x,this.Model.position.z)){this.Model.position.x+=this.Look.x}if(DetectCollision(collision_map,5,this.Model.position.x,a.z)){this.Model.position.z+=this.Look.z}$("#debug").html("X:"+this.Model.position.x+"<br/>Z:"+this.Model.position.z)};